import React, {useState ,useEffect} from 'react'
import { Link } from "react-router-dom";
import axios from "axios"

export default function Iphone() {
const [iphone, setiphone] = useState([])    

useEffect(() => {
  axios ("http://localhost:2022/iphones")
  .then((res)=>{
      setiphone(res.data.products)
  })
console.log(iphone);
 }, [ ])
 let order = 1;
  return (

    <div>
    <section className="internal-page-wrapper top-100">
        <div className="container">
          <div className="row justify-content-center text-center">
            <div className="col-12">
              <div className="title-wraper bold">Iphones</div>
              <div className="brief-description">
                The best for the brightest.
              </div>
            </div>
            </div>
            {iphone.map((products)=>{
              let id=products.product_url
              let price=products.price_range
              let Brief=products.product_brief_description
              let description=products.product_description
              let image=products.product_img
              let name=products.product_name
              let url=products.product_url
              let StartPrice=products.statrting_price
              let productPage = "/iphone/" + id;
              let order1 = 1;
              let order2 = 2;
              if (order !== 1) {
                order1 = 2;
                order2 = 1;
                order--;
              } else {
                order++;
              }

              let productDiv = (
                <div
                  key={id}
                  className="row justify-content-center text-center product-holder h-100 top-100 bottom-100"
                >
                  <div className={`col-sm-12 col-md-6 my-auto order-${order1}`}>
                    <div className="product-title">{name}</div>
                    <div className="product-brief">{Brief}</div>
                    <div className="starting-price">
                      {`Starting at ${StartPrice}`}
                    </div>
                    <div className="monthly-price">{price}</div>
                    <div className="links-wrapper">
                      <ul> 
                        <li>
                          <Link to={productPage}>Learn more</Link>
                        </li>
                      </ul>
                    </div>
                  </div>

                  <div className={`col-sm-12 col-md-6 order-${order2}`}>
                    <div className="prodict-image">
                      <img src={image} alt="" />
                    </div>
                  </div>
               </div>
              );
              return productDiv;

            })
            }
            </div>
     </section>
    </div>
  )
}
